            if course.is_full:
                messages.error(request, f'Cannot enroll - course is full ({course.enrolled_count}/{course.openings})')
                return redirect('/teacher-course-students/' + str(course_id) + '/')

            # Check if already enrolled
            if Enrollment.objects.filter(student=student, course=course).exists():
                messages.error(request, f'{student.first_name} {student.last_name} is already enrolled')
                return redirect('/teacher-course-students/' + str(course_id) + '/')

            # Create enrollment
            Enrollment.objects.create(
                student=student,
                course=course,
                enrolled_by=teacher,
                enrollment_deadline=course.enrollment_deadline
            )

            messages.success(request, f'Successfully enrolled {student.first_name} {student.last_name} in {course.name}')
            return redirect('/teacher-course-students/' + str(course_id) + '/')

        except Exception as e:
            messages.error(request, f'Error: {str(e)}')

    # Get all students not enrolled in this course
    enrolled_student_ids = Enrollment.objects.filter(course=course).values_list('student_id', flat=True)
    available_students = Student.objects.exclude(id__in=enrolled_student_ids).select_related('user')

    return render(request, 'direct_enroll.html', {
        'course': course,
        'available_students': available_students
    })


@teacher_required
def update_deadline_view(request, course_id):
    """Update course enrollment deadline - Teachers only"""
    if request.user.is_superuser:
        teacher = Teacher.objects.first()
    else:
        teacher = request.user.teacher_profile

    course = get_object_or_404(Course, id=course_id)

    # Verify teacher teaches this course (skip for admin)
    if not request.user.is_superuser and teacher:
        if not course.teachers.filter(id=teacher.id).exists():
            messages.error(request, 'You are not assigned to this course')
            return redirect('/teacher-dashboard/')

    if request.method == 'POST':
        try:
            deadline_str = request.POST.get('enrollment_deadline')

            if deadline_str:
                from django.utils.dateparse import parse_datetime
                deadline = parse_datetime(deadline_str)
                if not deadline:
                    messages.error(request, 'Invalid date format')
                    return redirect('/manage-course/' + str(course_id) + '/')
            else:
                deadline = None

            old_deadline = course.enrollment_deadline
            course.enrollment_deadline = deadline
            course.save()

            # Update pending enrollment requests
            EnrollmentRequest.objects.filter(
                course=course,
                status='pending'
            ).update(enrollment_deadline=deadline)

            if deadline:
                messages.success(request, f'Enrollment deadline updated to {deadline.strftime("%B %d, %Y %I:%M %p")}')
            else:
                messages.success(request, 'Enrollment deadline removed')

            return redirect('/manage-course/' + str(course_id) + '/')

        except Exception as e:
            messages.error(request, f'Error: {str(e)}')

    return redirect('/manage-course/' + str(course_id) + '/')


@teacher_required
def manage_course_view(request, course_id):
    """Course management page - Teachers only"""
    if request.user.is_superuser:
        teacher = Teacher.objects.first()
    else:
        teacher = request.user.teacher_profile

    course = get_object_or_404(Course, id=course_id)

    # Verify teacher teaches this course (skip for admin)
    if not request.user.is_superuser and teacher:
        if not course.teachers.filter(id=teacher.id).exists():
            messages.error(request, 'You are not assigned to this course')
            return redirect('/teacher-dashboard/')

    # Get course statistics
    enrollments = Enrollment.objects.filter(course=course).select_related('student')
    pending_requests = EnrollmentRequest.objects.filter(
        course=course,
        status__in=['pending', 'waitlisted']
    ).select_related('student')

    # Get all students not enrolled
    enrolled_student_ids = enrollments.values_list('student_id', flat=True)
    available_students = Student.objects.exclude(id__in=enrolled_student_ids).select_related('user')

    return render(request, 'manage_course.html', {
        'course': course,
        'enrollments': enrollments,
        'pending_requests': pending_requests,
        'available_students': available_students,
        'teacher': teacher
    })

""" Test script for the new grading system Tests: 1. Student registration without GPA 2. Enrollment without grades 3. Teacher grade assignment (numeric) 4. Automatic letter grade conversion 5. Automatic GPA calculation """
import requests import json
BASE_URL = "http://127.0.0.1:8000"
def print_section(title): print("\n" + "="*60) print(f" {title}") print("="*60)
def test_student_registration(): """Test that students can register without providing GPA""" print_section("TEST 1: Student Registration (No GPA)")
data = { "username": "test_student_2025", "email": "test2025@example.com", "password": "TestPass123", "first_name": "Test", "last_name": "Student", "age": 20 # Notice: NO GPA field! }
print("Registering student WITHOUT GPA field...") print(f"Data: {json.dumps(data, indent=2)}")
response = requests.post(f"{BASE_URL}/auth/register/student/", json=data) print(f"\nStatus: {response.status_code}") print(f"Response: {json.dumps(response.json(), indent=2)}")
if response.status_code == 201: print("\n✅ SUCCESS: Student registered without GPA!") return response.json().get('student_id') else: print("\n❌ FAILED: Could not register student") return None
def test_teacher_login(): """Login as a teacher to get credentials""" print_section("TEST 2: Teacher Login")
# You need to have a teacher account. Create one first or use existing data = { "username": "teacher1", # Update with your teacher username "password": "teacher123" # Update with your teacher password }
print(f"Logging in as teacher: {data['username']}")
response = requests.post(f"{BASE_URL}/auth/login/", json=data) print(f"Status: {response.status_code}")
if response.status_code == 200: print("✅ SUCCESS: Teacher logged in") return True else: print("❌ FAILED: Teacher login failed") print("Note: Create a teacher account first or update credentials in this script") return False
def test_enrollment_request(student_id, course_id=1): """Test enrollment request without grade""" print_section("TEST 3: Enrollment Request (No Grade)")
data = { "student_id": student_id, "course_id": course_id, "notes": "Testing new enrollment system" }
print(f"Student {student_id} requesting enrollment in course {course_id}...") print(f"Data: {json.dumps(data, indent=2)}")
response = requests.post(f"{BASE_URL}/enrollment/request/", json=data) print(f"\nStatus: {response.status_code}") print(f"Response: {json.dumps(response.json(), indent=2)}")
if response.status_code == 201: print("\n✅ SUCCESS: Enrollment requested without grade!") return response.json().get('request_id') else: print("\n❌ FAILED: Could not request enrollment") return None
def test_approve_enrollment(request_id, teacher_id=1): """Test enrollment approval without grade""" print_section("TEST 4: Approve Enrollment (No Grade Assigned)")
data = { "teacher_id": teacher_id }
print(f"Teacher {teacher_id} approving enrollment request {request_id}...") print(f"Data: {json.dumps(data, indent=2)}")
response = requests.post(f"{BASE_URL}/teachers/request/{request_id}/approve/", json=data) print(f"\nStatus: {response.status_code}") print(f"Response: {json.dumps(response.json(), indent=2)}")
if response.status_code == 200: print("\n✅ SUCCESS: Enrollment approved WITHOUT grade!") return response.json().get('enrollment_id') else: print("\n❌ FAILED: Could not approve enrollment") return None
def test_update_grade(enrollment_id, teacher_id=1): """Test assigning numeric grade with automatic letter grade conversion""" print_section("TEST 5: Assign Numeric Grade (Letter Auto-Converts)")
test_grades = [ (95.0, "A"), (87.5, "B"), (72.0, "C"), (65.0, "D"), (50.0, "F") ]
for numeric_grade, expected_letter in test_grades: data = { "teacher_id": teacher_id, "grade": numeric_grade }
print(f"\n--- Testing grade: {numeric_grade} ---") print(f"Expected letter grade: {expected_letter}") print(f"Data: {json.dumps(data, indent=2)}")
response = requests.put(f"{BASE_URL}/teachers/enrollment/{enrollment_id}/grade/", json=data) print(f"Status: {response.status_code}")
if response.status_code == 200: result = response.json() print(f"Response: {json.dumps(result, indent=2)}")
actual_letter = result.get('new_letter_grade') if actual_letter == expected_letter: print(f"✅ Correct: {numeric_grade} → {actual_letter}") else: print(f"❌ Wrong: Expected {expected_letter}, got {actual_letter}") else: print(f"❌ FAILED: Status {response.status_code}") print(f"Response: {response.text}")
print("\n✅ SUCCESS: Numeric grades convert to letter grades!")
def test_student_gpa(student_id): """Test that GPA is calculated automatically""" print_section("TEST 6: Automatic GPA Calculation")
print(f"Fetching student {student_id} details to check GPA...")
response = requests.get(f"{BASE_URL}/students/{student_id}/") print(f"Status: {response.status_code}")
if response.status_code == 200: student_data = response.json() print(f"Response: {json.dumps(student_data, indent=2)}")
gpa = student_data.get('gpa') print(f"\n Student GPA: {gpa}")
if gpa is not None: print("✅ SUCCESS: GPA is calculated automatically!") else: print("❌ FAILED: GPA is None") else: print("❌ FAILED: Could not fetch student")
def test_invalid_grade(enrollment_id, teacher_id=1): """Test that invalid grades are rejected""" print_section("TEST 7: Grade Validation")
invalid_grades = [ (150, "Grade > 100"), (-10, "Negative grade"), ("ABC", "Non-numeric grade") ]
for invalid_grade, description in invalid_grades: data = { "teacher_id": teacher_id, "grade": invalid_grade }
print(f"\n--- Testing invalid grade: {description} ---") print(f"Value: {invalid_grade}")
response = requests.put(f"{BASE_URL}/teachers/enrollment/{enrollment_id}/grade/", json=data) print(f"Status: {response.status_code}") print(f"Response: {json.dumps(response.json(), indent=2)}")
if response.status_code == 400: print(f"✅ Correctly rejected: {description}") else: print(f"❌ Should have rejected: {description}")
print("\n✅ SUCCESS: Invalid grades are properly validated!")
